mapper.insert:
insert into tb_dict (id, code, name, 
  stateName, stateCode, companyId
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{stateName,jdbcType=VARCHAR}, #{stateCode,jdbcType=INTEGER}, #{companyId,jdbcType=INTEGER}
  )

mapper.select:
select stateCode
from tb_dict
where code = #{code} and stateName = #{stateName} and companyId = #{companyId};

select stateName
from tb_dict
where code = #{code} and stateCode = #{stateCode} and companyId = #{companyId};

select  
from tb_dict
where name = #{name} and companyId = #{companyId};
