mapper.select:
select 

from tb_task
where companyId = #{companyId,jdbcType=INTEGER} and  prevType = #{prevType,jdbcType=INTEGER} 

    and type = #{type,jdbcType=INTEGER}

 and state > #{state,jdbcType=INTEGER} order by step desc limit #{page},#{size}

select count(*)
from tb_task
where companyId = #{companyId,jdbcType=INTEGER} and  prevType = #{prevType,jdbcType=INTEGER}  

    and type = #{type,jdbcType=INTEGER}
 and state > #{state,jdbcType=INTEGER}

select 

from tb_task
where postIdList like concat('%',#{postId},'%') 

    and prevType = #{prevType,jdbcType=INTEGER}


    and type = #{type,jdbcType=INTEGER}
